//https://leetcode.com/problems/separate-black-and-white-balls/
struct Solution;

impl Solution {
    pub fn minimum_steps(s: String) -> i64 {
        let mut zeroes = 0;
        let mut shift_zero_to_its_position = 0;
        for (i, &c) in s.as_bytes().iter().enumerate() {
            if c == 48 {
                shift_zero_to_its_position += i - zeroes;
                zeroes += 1;
            }
        }
        shift_zero_to_its_position as i64
    }
}

fn main() {
    println!("Hello, world!");
}

#[cfg(test)]
mod tests {
    fn testcases() -> Vec<(String, i64)> {
        vec![
("001".to_string(), 0),
("0011000010".to_string(), 11),
("001110001110001".to_string(), 27),
("111111111111111111111111111111111111111110011".to_string(), 82),
("1111110000000000000000000000000000000000001011".to_string(), 223),
("".to_string(), 0),
("".to_string(),245899 ),
("".to_string(), 17443428),
      ]
    }

    use super::Solution;
    #[test]
    fn test_minimum_steps() {
        for (s, want) in testcases() {
            assert_eq!(Solution::minimum_steps(s), want);
        }
    }
}
