struct Solution;

impl Solution {
    pub fn seconds_to_remove_occurrences_pass1(s: String) -> i32 {
        let (mut i, mut j) = (0, -1);
        let mut s = s.into_bytes();
        let mut c = 1;
        while c > 0 {
            (i, c) = (0, 0);
            while i < s.len()-1 {
                if s[i] == b'0' && s[i+1] == b'1' {
                    s[i] = s[i] + s[i+1];
                    s[i+1] = s[i] - s[i+1];
                    s[i] = s[i] - s[i+1];
                    i+=2;
                    c+=1;
                } else {
                    i+=1;
                }
            }
            j+=1;
        }
        j
    }

    pub fn seconds_to_remove_occurrences(s: String) -> i32 {
        let (mut zeros, mut ans) = (0,0);
        let s = s.into_bytes();
        for i in 0..s.len() {
            if s[i] == 48 {
                zeros += 1;
            } else if zeros > 0 {
                ans = (ans+1).max(zeros);
            }
        }
        ans
    }
}

